(8,	'Crac\'tine riz et châtaigne 250g	',	'VALPIBIO	',	'5.90',	'https://www.greenweez.com/valpibio-crac-tine-riz-et-chataigne-250g-p83575?objectID=83575_0_0&queryID=73d9e0aa76356b1470cb433af19c2c91&isSearch=true	',	'https://static.greenweez.com/images/products/83000/600/valpibio-crac-tine-riz-et-chataigne-250g.jpg	',	'2022-11-20 11:21:08',	NULL,	'alimentaire'),
(9,	'Gratte langue ayurvédique en cuivre	',	'BAMBAW	',	'4.90',	'https://www.greenweez.com/bambaw-gratte-langue-ayurvedique-en-cuivre-p116670?objectID=116670_0_0&queryID=dbc35c0b2d99f332634cb7047e69228c&isSearch=true	',	'https://static.greenweez.com/images/products/116000/600/bambaw-gratte-langue-ayurvedique-en-cuivre.jpg	',	'2022-11-20 11:21:47',	NULL,	'hygiéne'),
(10,	'Chou rouge lacto-fermenté en bocal	',	'Nutriform	',	'3',	'https://www.naturalia.fr/chou-rouge-lf-300g?gclid=Cj0KCQjw8amWBhCYARIsADqZJoVI16rFlJojJzQqsflejqWsO7P32GHxrmSERA0uij1zCaHcgXsXox8aAtdZEALw_wcB	',	'https://media.naturalia.fr/media/catalog/product/cache/92d3ef1420f22584d764d2b01ae36138/3/0/3022820420315.p1-1500j.jpg	',	'2022-11-20 11:22:25',	NULL,	'alimentaire'),
